function sanitizeEmail(email) {
  if (!email) return '';
  const cleanEmail = email.trim().toLowerCase();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(cleanEmail) ? cleanEmail : '';
}

function sanitizePhone(phone) {
  if (!phone) return '';

  const digits = phone.replace(/\D/g, '');

  if (digits.length === 10) {
    return `(${digits.slice(0, 2)}) ${digits.slice(2, 6)}-${digits.slice(6)}`;
  } else if (digits.length === 11) {
    return `(${digits.slice(0, 2)}) ${digits.slice(2, 7)}-${digits.slice(7)}`;
  }

  return digits;
}



return $input.all().map(item => {  
  const venda = item.json;
  venda.Email = sanitizeEmail(venda.Email);
  venda.Telefone = sanitizePhone(venda.Telefone);
  venda.Data_Venda = new Date(venda.Data_Venda).toISOString().split('T')[0]; // YYYY-MM-DD
  venda.Quantidade = parseInt(venda.Quantidade);
  venda.Preco_Unitario = parseFloat(venda.Preco_Unitario);
  venda.Valor_Total = parseFloat(venda.Valor_Total);
  return { json: venda };
});

const items = $input.all();

// Função para limpar e validar e-mail
function sanitizeEmail(email) {
  if (!email) return '';
  const cleanEmail = email.trim().toLowerCase();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(cleanEmail) ? cleanEmail : '';
}

// Função para limpar e formatar telefone
function sanitizePhone(phone) {
  if (!phone) return '';

  // Remove tudo que não for dígito
  const digits = phone.replace(/\D/g, '');

  // Opção A: Retornar apenas os números
  // return digits;

  // Opção B: Formatar com máscara (se possível)
  if (digits.length === 10) {
    // Ex: 5198765432 => (51) 9876-5432
    return `(${digits.slice(0, 2)}) ${digits.slice(2, 6)}-${digits.slice(6)}`;
  } else if (digits.length === 11) {
    // Ex: 51987654321 => (51) 98765-4321
    return `(${digits.slice(0, 2)}) ${digits.slice(2, 7)}-${digits.slice(7)}`;
  }

  // Se não bater com nenhum padrão esperado, retorna só os números
  return digits;
}

// Processa todos os itens
return items.map(item => {
  const data = item.json;

  data.Email = sanitizeEmail(data.Email);
  data.Telefone = sanitizePhone(data.Telefone);

  return { json: data };
});
